// Input.js
import React, { useState } from 'react';
import logo from '../assets/logo.png';

function Input({ addContent, closeForm }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState('');

  // 이미지 파일 선택 시 미리보기 설정
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => setImage(reader.result);
      reader.readAsDataURL(file);
    } else {
      setImage('');
    }
  };

  // 입력 데이터 추가
  const handleAdd = () => {
    const finalImage = image || logo;
    if (title && description) {
      addContent({ title, description, image: finalImage });
      setTitle('');
      setDescription('');
      setImage('');
      closeForm(); // ✅ 추가 후 폼 닫기
    } else {
      alert('제목과 설명을 입력해주세요!');
    }
  };

  return (
    <div style={{
      position: 'fixed',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      backgroundColor: 'white',
      padding: '20px',
      border: '2px solid pink',
      borderRadius: '10px',
      zIndex: 1000
    }}>
      <h3>콘텐츠 추가</h3>
      <input
        type="text"
        placeholder="제목"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        style={{ margin: '5px', padding: '5px', width: '100%' }}
      />
      <input
        type="text"
        placeholder="설명"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        style={{ margin: '5px', padding: '5px', width: '100%' }}
      />
      <input
        type="file"
        onChange={handleImageChange}
        style={{ margin: '5px' }}
      />
      <div style={{ marginTop: '10px' }}>
        <button onClick={handleAdd} style={{ marginRight: '5px' }}>추가</button>
        <button onClick={closeForm}>닫기</button>
      </div>
    </div>
  );
}

export default Input;
