// Input.js
import React, { useState } from 'react';
import logo from '../assets/logo.png';

function Input({ addContent }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState('');

  // 이미지 파일 선택 시 미리보기 설정
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => setImage(reader.result);
      reader.readAsDataURL(file);
    } else {
      setImage(''); // 파일 취소 시 초기화
    }
  };

  // 입력 데이터 추가
  const handleAdd = () => {
    // 이미지가 없으면 기본 로고 사용
    const finalImage = image || logo;
    if (title && description) {
      addContent({ title, description, image: finalImage });
      setTitle('');
      setDescription('');
      setImage('');
    } else {
      alert('제목과 설명을 입력해주세요!');
    }
  };

  return (
    <div style={{ marginBottom: '20px', textAlign: 'center' }}>
      <input
        type="text"
        placeholder="제목"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        style={{ margin: '5px', padding: '5px' }}
      />
      <input
        type="text"
        placeholder="설명"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        style={{ margin: '5px', padding: '5px' }}
      />
      <input
        type="file"
        onChange={handleImageChange}
        style={{ margin: '5px' }}
      />
      <button onClick={handleAdd} style={{ padding: '5px 10px' }}>추가</button>

      {image && (
        <div style={{ marginTop: '10px' }}>
          <h4>미리보기</h4>
          <img src={image} alt="미리보기" style={{ width: '100px', borderRadius: '8px' }} />
        </div>
      )}
    </div>
  );
}

export default Input;
